
CREATE TABLE PLAN (CODIGO_PLAN VARCHAR2(10) PRIMARY KEY, 
                  NOMBRE VARCHAR2(50), 
                  ENTIDAD_FINANCIADORA VARCHAR2(50));

CREATE TABLE PROYECTO(CODIGO_PROYECTO CHAR(2) PRIMARY KEY,
                      CODIGO_PLAN VARCHAR2(10),
                      NOMBRE VARCHAR2(15),
                      FECHA_INICIO DATE,
                      FECHA_FIN DATE,
                      PRESUPUESTO NUMBER(6));
ALTER TABLE PROYECTO 
ADD FOREIGN KEY (CODIGO_PLAN) REFERENCES PLAN(CODIGO_PLAN);

CREATE TABLE INVESTIGADOR (DNI_INVESTIGADOR VARCHAR2(10) PRIMARY KEY,
                          NOMBRE VARCHAR2(40),
                          CIUDAD VARCHAR2(25),
                          TELEFONO VARCHAR2(15));                    
                          
CREATE TABLE ASIGNADO_A(CODIGO_PROYECTO CHAR(2),
                        DNI_INVESTIGADOR VARCHAR2(10),
                        FECHA_INICIO DATE,
                        FECHA_FIN DATE,
                        TIPO_PARTICIPACION VARCHAR2(20),
                        PRIMARY KEY(CODIGO_PROYECTO, DNI_INVESTIGADOR ));
ALTER TABLE ASIGNADO_A 
ADD FOREIGN KEY ( DNI_INVESTIGADOR ) REFERENCES INVESTIGADOR( DNI_INVESTIGADOR );
ALTER TABLE ASIGNADO_A 
ADD FOREIGN KEY (CODIGO_PROYECTO) REFERENCES PROYECTO (CODIGO_PROYECTO);


